mapper.select:
SELECT * FROM section_monitor


    AND monitor_point_name LIKE CONCAT('%', #{monitorPointName}, '%')


    AND reservoir_name LIKE CONCAT('%', #{reservoirName}, '%')


    AND year >= #{yearMin}


    AND year <= #{yearMax}


    AND month >= #{monthMin}


    AND month <= #{monthMax}


    AND oxygen >= #{oxygenMin}


    AND oxygen <= #{oxygenMax}


    AND potassium_permanganate >= #{potassiumPermanganateMin}


    AND potassium_permanganate <= #{potassiumPermanganateMax}


    AND cod >= #{codMin}


    AND cod <= #{codMax}


    AND flow >= #{flowMin}


    AND flow <= #{flowMax}


    AND water_depth >= #{waterDepthMin}


    AND water_depth <= #{waterDepthMax}


    AND total_nitrogen >= #{totalNitrogenMin}


    AND total_nitrogen <= #{totalNitrogenMax}


    AND total_phosphorus >= #{totalPhosphorusMin}


    AND total_phosphorus <= #{totalPhosphorusMax}


ORDER BY id DESC
